mapper.select:
SELECT count(*)
FROM information_schema.TABLES
WHERE table_name = #{tableName}

mapper.update:
CREATE TABLE ${name}
(
    `id`          bigint(20) NOT NULL AUTO_INCREMENT,
    `name`        varchar(255) DEFAULT NULL,
    `age`         int(11)      DEFAULT NULL,
    `create_date` datetime     DEFAULT NULL,
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8;
